import React, { useState } from "react";

function CreateArea(props) {

  let title = '';
  let content = '';
  function handleTitle(event) {
    title = event.target.value;
  }
  function handleContent(event) {
    content = event.target.value;
  }
  function onClickHandle(event) {
    props.addItem(title, content);
    event.preventDefault();
  }
  return (
    <div>
      <form>
        <input onChange={handleTitle} name="title" placeholder="Title" />
        <textarea onChange={handleContent} name="content" placeholder="Take a note..." rows="3" />
        <button onClick={() => props.addItem(title, content)}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
