import React from "react";
import { items, updateItems } from './items';

function Note(props) {

  function deleteItem() {
    items = items.map(item => (item.title !== props.title && item.content !== props.content));
    updateItems([...items]);
  }
  return (
    <div className="note">
      <h1>{props.title}</h1>
      <p>{props.content}</p>
      <button onClick={deleteItem}>DELETE</button>
    </div>
  );
}

export default Note;
