import React from "react";

function App() {
  const [time, gettime] = React.useState(time);
  function getTime() {
    gettime(Date().toLocaleTimeString());
  }

  return (
    <div className="container">
      <h1>{time}</h1>
      <button onClick='gettime'>Get Time</button>
    </div>
  );
}

export default App;
