import React from "react";

function App() {

  const [count, updateCountto] = React.useState(0)

  function increase() {
    updateCountto(count + 1);
  }
  function decrease() {
    updateCountto(count - 1);
  }


  return <div className="container">
    <h1>{count}</h1>
    <button onClick={increase}>+</button>
    <button onClick={decrease}>+</button>
  </div>;
}

export default App;
